.header__category {
	 color: #fff;
}
 .header {
	 background: #000;
}
 .top-bar {
	 background: #7200dd;
}
 .top-bar__nav {
	 color: #fff;
}
 #cart-button {
	 color: #fff;
}
 #cart-button {
	 color: #fff;
}
 #button-whatsapp {
	 width: 100%;
	 color: #fff;
	 padding: 15px;
	 background-color: #468d7e;
	 border: none;
	 border-radius: 4px;
	 margin-top: 10px;
	 display: flex;
	 text-decoration: none !important;
	 align-items: center;
	 justify-content: center;
	 flex-direction: row-reverse;
	 box-shadow: #000 0 5px 10px 0;
}
 #button-whatsapp .wpp-icon {
	 width: 20px;
	 margin-right: 10px;
}
 .product__buy button, #goToCart {
	 width: 100%;
}
 #goToCart {
	 margin-top: 10px;
}
 .product-card__buy.fade {
	 opacity: 1;
}
 #whatsapp-button-card {
	 padding-left: 0;
	 padding-right: 0;
}
 .stamps__league {
	 list-style: none;
	 display: flex;
	 align-items: center;
	 flex-wrap: nowrap;
	 overflow-x: auto;
	 padding: 0;
	 margin: 0;
	 max-width: 100%;
}
 .stamps__league li:not(:last-child), .timer {
	 margin-right: 1.5rem;
}
 .stamps__league > li {
	 padding-right: 0.5rem;
	 min-width: auto;
}
 .stamps__league > li {
	 min-width: 180px;
	 max-width: 180px;
	 padding: 1rem 0;
	 text-align: center;
}
 .stamps__league img {
	 max-height: none;
}
 .stamps__league li {
	 display: flex;
	 justify-content: center;
	 align-items: center;
	 border-radius: 50%;
	 background-color: #fff;
	 box-shadow: 0.0625rem 0.0625rem 0.25rem #979798;
}
 #col-fav .stamps__league li {
	 display: flex;
	 justify-content: center;
	 align-items: center;
	 border-radius: 0;
	 background-color: #fff;
	 box-shadow: none;
}
 @media (min-width: 767px) {
	 .stamps__league li {
		 width: 8.7375rem;
		 height: 8.7375rem;
	}
	 #col-fav .stamps__league li {
		 width: 12.7375rem;
		 height: 24.7375rem;
	}
	 #col-fav img {
		 max-width: 240px;
	}
	 .footer__stamps__league img {
		 max-width: 80px;
	}
	 #col-fav .stamps__league > li {
		 min-width: 240px;
		 max-width: 300px;
		 padding: 1rem 0;
		 text-align: center;
	}
}
 @media (max-width: 767px) {
	 .stamps__league li {
		 width: 10.7375rem;
		 height: 10.7375rem;
	}
	 .footer__stamps__league img {
		 max-width: 70px !important;
	}
	 #col-fav .stamps__league li {
		 width: 10.7375rem;
		 height: 12.7375rem;
	}
	 #col-fav img {
		 max-width: 135px;
	}
	 #col-fav .stamps__league > li {
		 min-width: 135px;
	}
}
 .footer__stamps__league p {
	 font-weight: bold;
	 font-size: 2rem;
}
 #modal-stamps .tabs-container li a {
	 color: #b5b5b5;
}
 #modal-stamps .tabs-container li.active a, #modal-stamps .tabs-container li a:hover {
	 color: #242424 !important;
}
 #modal-stamps .tabs-container li.active, #modal-stamps .tabs-container li:hover {
	 border-bottom: 0.125rem solid #242424 !important;
}
 #modal-stamps .modal-body {
	 max-width: 52.0625rem;
	 min-width: 18.75rem;
	 background-color: #fff;
	 border-radius: 0.5rem;
	 padding: 1.5rem 1rem;
}
 #modal-stamps .tabs-container .tabs .tab {
	 padding-bottom: 0.375rem;
}
 #modal-stamps .tabs-container .tabs .tab:first-child {
	 margin-left: 0;
}
 #modal-stamps .tabs-container li {
	 display: inline-block;
	 flex-grow: 1;
	 text-align: center;
	 text-transform: uppercase;
	 font-size: 0.9375rem;
	 margin-left: 0.5rem;
}
 #modal-stamps .tabs a {
	 text-decoration: none !important;
}
 #modal-stamps .modal-dialog {
	 max-width: 50%;
	 width: 100%;
